//program to demonstrate on assignment operator
package org.tnsif.operators;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=12, y=7;
		//x-=y;//x=x-y
		x+=y;
		System.out.println(x);
		x-=y;
		System.out.println(x);
		
	}

}
